import React from "react";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
//import Switch from "./Switch";

export default function App() {
  return (
    <Router>
      <div>
        <nav className="nav">
          <h2 className="title">Welcome to So Hyun's World</h2>
          <div className="desc">
            안녕하세요, 저는 햇뵹아리 프로그래머 입니다. 누추한 곳에
            찾아와주셔서 감사합니다^~^
          </div>
          <header className="header">
            <div>
              <Link to="/">Home </Link>
            </div>
            <div>
              <Link to="/blog">BLOG </Link>
            </div>
            <div>
              <Link to="/github">Let's GITit!!</Link>
            </div>
          </header>
        </nav>

        {/* A <Switch> looks through its children <Route>s and
            renders the first one that matches the current URL. */}
        <Switch>
          {" "}
          {/* 주소로 가는 부분 */}
          <Route path="/github">
            <Github />
          </Route>
          <Route path="/blog">
            <Blog />
          </Route>
          <Route path="/">
            <Home />
          </Route>
        </Switch>
      </div>
    </Router>
  );
}

function Home() {
  return (
    <div className="content">
      <div className="grid">
        <div>
          <img src="https://picsum.photos/500"></img>
        </div>
        <div className="home-text">이렇게저렇게요롷게</div>
      </div>
    </div>
  );
}

function Blog(props) {
  return (
    <div>
      <div className="content">
        Blog Address: http://programming24-7.tistory.com/
      </div>
    </div>
  );
}

function Github() {
  return <div className="content">Github ID: sokim1616</div>;
}

